import axios from "axios";
import React, { useEffect, useState } from "react";
import { BASE_URL, DELETE_USER, USERS } from "../../Api/APi";
import Cookie from "cookie-universal";
import Claer from "../../assets/clear.png";
import edit from "../../assets/edit.png";
import Logout from "../../pages/auth/Logout";

const Users = () => {
  const cookie = Cookie();
  const token = cookie.get("Bearer");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios
      .get(`${BASE_URL}/${USERS}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => setUsers(response.data.data.users))
      .catch((error) => console.log(error));
  }, [token]);

  async function DeleteUser(id) {
    try {
      await axios.delete(`${BASE_URL}/${DELETE_USER}/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      // تحديث القائمة بعد الحذف
      setUsers(users.filter((user) => user._id !== id));
    } catch (error) {
      console.log(error);
    }
  }

  const dataShow = users.map((item, index) => (
    <tr key={item._id || index}>
      <th scope="row">{index + 1}</th>
      <td>{item.name}</td>
      <td>{item.email}</td>
      <td>{item.role}</td>
      <td>
        <button
          onClick={() => DeleteUser(item._id)}
          className="btn btn-sm btn-danger me-2"
          title="Delete User"
        >
          <img style={{ width: "16px" }} src={Claer} alt="Delete" />
        </button>
        <button className="btn btn-sm btn-primary" title="Edit User">
          <img style={{ width: "16px" }} src={edit} alt="Edit" />
        </button>
      </td>
    </tr>
  ));

  return (
    <div className="container mt-4">
      <Logout />
      <h2 className="mb-4">Users List</h2>
      <table className="table table-striped table-hover align-middle">
        <thead className="table-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>{dataShow}</tbody>
      </table>
    </div>
  );
};

export default Users;
