import { faUsers } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import "./bars.css";
import { Menu } from "../../context/menuContext";
import { WindowSize } from "../../context/WindowContext";

const SideBar = () => {
  const { isOpen } = useContext(Menu);
  const { windowSize } = useContext(WindowSize);

  // في الشاشات الصغيرة، نخفي السايد بار بإزاحته لليسار لو مش مفتوح
  // في الشاشات الكبيرة، نعرضه على طول حسب isOpen
  const sidebarStyle = {
    width: isOpen ? "240px" : "fit-content",
    left:
      windowSize < 768
        ? isOpen
          ? "0" // لما مفتوح على موبايل، يبان
          : "-100%" // لما مش مفتوح على موبايل، يختفي برا الشاشة
        : "0", // دايمًا ظاهر على الشاشات الكبيرة
    transition: "left 0.3s ease", // عشان يظهر ويختفي بسلاسة
    position: "fixed",
    top: "70px", // لو عندك top bar ارتفاعه 70px
    height: "calc(100vh - 70px)", // عشان يملى طول الشاشة بعد خصم الـ top bar
    backgroundColor: "white",
    boxShadow: "0 0 0 1px rgba(0, 0, 0, 0.1)",
    padding: "20px 10px",
    zIndex: 1000,
  };

  return (
    <div className="side-bar" style={sidebarStyle}>
      <NavLink
        to={"users"}
        className={({ isActive }) =>
          isActive ? "nav-icon active" : "nav-icon"
        }
      >
        <FontAwesomeIcon
          icon={faUsers}
          className="icon"
          style={{ padding: isOpen ? "10px 8px 10px 15px" : "10px 13px" }}
        />
        <span className="label" style={{ display: isOpen ? "block" : "none" }}>
          Users
        </span>
      </NavLink>
    </div>
  );
};

export default SideBar;
