import React, { useEffect, useState } from "react";

const EditUser = () => {
  const userId = "6844b5c6ea49bec844ce51c4"; // غيره حسب الحاجة

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // تحميل بيانات المستخدم عند التحميل
  useEffect(() => {
    async function fetchUser() {
      try {
        const res = await fetch(
          `http://localhost:4000/api/user/users/${userId}`
        );
        if (!res.ok) throw new Error("لم يتم العثور على المستخدم");
        const user = await res.json();
        setName(user.name || "");
        setEmail(user.email || "");
      } catch (err) {
        setError(err.message);
      }
    }
    fetchUser();
  }, [userId]);

  // دالة تحديث المستخدم
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    try {
      const res = await fetch(
        `http://localhost:4000/api/user/users/${userId}`,
        {
          method: "PATCH", // أو PATCH حسب API عندك
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name, email }),
        }
      );
      if (!res.ok) throw new Error("حدث خطأ أثناء التحديث");
      const data = await res.json();
      setMessage("تم تحديث بيانات المستخدم بنجاح!");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div
      style={{
        maxWidth: 400,
        margin: "2rem auto",
        padding: "1rem",
        backgroundColor: "#f5f5f5",
        borderRadius: 8,
        fontFamily: "Arial, sans-serif",
        direction: "rtl",
      }}
    >
      <h2>تعديل بيانات المستخدم</h2>
      <form onSubmit={handleSubmit}>
        <label
          htmlFor="name"
          style={{
            display: "block",
            marginTop: "1rem",
            marginBottom: "0.3rem",
            fontWeight: "bold",
          }}
        >
          الاسم:
        </label>
        <input
          id="name"
          type="text"
          value={name}
          required
          onChange={(e) => setName(e.target.value)}
          style={{
            width: "100%",
            padding: "0.5rem",
            borderRadius: 4,
            border: "1px solid #ccc",
            boxSizing: "border-box",
          }}
        />

        <label
          htmlFor="email"
          style={{
            display: "block",
            marginTop: "1rem",
            marginBottom: "0.3rem",
            fontWeight: "bold",
          }}
        >
          البريد الإلكتروني:
        </label>
        <input
          id="email"
          type="email"
          value={email}
          required
          onChange={(e) => setEmail(e.target.value)}
          style={{
            width: "100%",
            padding: "0.5rem",
            borderRadius: 4,
            border: "1px solid #ccc",
            boxSizing: "border-box",
          }}
        />

        <button
          type="submit"
          style={{
            marginTop: "1.5rem",
            backgroundColor: "#007bff",
            color: "white",
            fontWeight: "bold",
            cursor: "pointer",
            border: "none",
            width: "100%",
            padding: "0.5rem",
            borderRadius: 4,
          }}
        >
          تحديث
        </button>
      </form>

      {message && (
        <p style={{ color: "green", marginTop: "1rem", fontWeight: "bold" }}>
          {message}
        </p>
      )}
      {error && (
        <p style={{ color: "red", marginTop: "1rem", fontWeight: "bold" }}>
          {error}
        </p>
      )}
    </div>
  );
};

export default EditUser;
