import axios from "axios";
import React, { useEffect, useState } from "react";
import { BASE_URL, USERS } from "../Api/APi";
import Cookie from "cookie-universal";
import Claer from "../assets/clear.png";
import edit from "../assets/edit.png";

const Users = () => {
  const cookie = Cookie();
  const token = cookie.get("Bearer");
  const [users, setusers] = useState([]);
  useEffect(() => {
    axios
      .get(`${BASE_URL}/${USERS}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((data) => setusers(data.data.data.users))
      .catch((error) => console.log(error));
  }, [token]);
  async function DeleteUser(id) {
    try {
      await axios.delete(`${BASE_URL}/${DELETE_USER}/${id}`);
    } catch (error) {
      console.log(error);
    }
  }
  const dataShow = users.map((item, index) => (
    <tr key={index}>
      <th>{index + 1}</th>
      <th>{item.name}</th>
      <th>{item.email}</th>
      <th>{item.role}</th>
      <th>
        <button onClick={() => DeleteUser(item.id)}>
          <img style={{ width: "13px" }} src={Claer} />
        </button>
        <button>
          <img style={{ width: "13px" }} src={edit} />
        </button>
      </th>
    </tr>
  ));
  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">id</th>
          <th scope="col">name</th>
          <th scope="col">email</th>
          <th scope="col">role</th>
          <th colSpan={2}>Actions</th>
        </tr>
      </thead>
      <tbody>{dataShow}</tbody>
    </table>
  );
};

export default Users;
