import React, { useState } from "react";
import { BASE_URL, LOGIN } from "../../Api/APi"; // لاحظ هنا LOGIN بدل REGISTER
import axios from "axios";
import "./form.css";
import "./button.css";
import { useNavigate } from "react-router-dom";
import Loading from "../../Loading/Loading";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // اسم الـ hook لازم بحروف صغيرة

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(`${BASE_URL}/${LOGIN}`, {
        email,
        password,
      });

      setLoading(false);
      setMessage("User logged in successfully");
      navigate("/"); // رجعه للصفحة الرئيسية
    } catch (error) {
      setLoading(false);
      if (
        error.response &&
        error.response.data &&
        error.response.data.message
      ) {
        setMessage(error.response.data.message);
      } else {
        setMessage("Login failed");
      }
    }
  };

  return (
    <>
      {loading && <Loading />}
      <div className="container">
        <div className="row h-100">
          <form onSubmit={handleSubmit} className="form custom-form">
            <div className="custom-form">
              <h1>Login Now</h1>
            </div>
            <div className="form-control">
              <input
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                placeholder="example@mail.com"
              />
              <label htmlFor="email">Email</label>
            </div>
            <div className="form-control">
              <input
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="********"
                minLength={6}
              />
              <label htmlFor="password">Password</label>
            </div>
            <button type="submit" className="btn btn-primary">
              Login
            </button>
            {message && <span className="error">{message}</span>}
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
