import React from "react";
import { Menubar } from "primereact/menubar";
import { InputText } from "primereact/inputtext";
import { Badge } from "primereact/badge";
import { Button } from "primereact/button";
import "primeicons/primeicons.css";
import "./Header.css"; // هنعمل ملف CSS صغير بعدين

const Header = () => {
  // عناصر القايمة (ممكن تعدل أو تضيف حسب حاجتك)
  const items = [
    {
      label: "Home",
      icon: "pi pi-home",
      command: () => {
        console.log("Home clicked");
      },
    },
    {
      label: "Friends",
      icon: "pi pi-users",
      command: () => {
        console.log("Friends clicked");
      },
    },
    {
      label: "Marketplace",
      icon: "pi pi-shopping-cart",
      command: () => {
        console.log("Marketplace clicked");
      },
    },
  ];

  // الجزء اللي على اليمين (أيقونات الإشعارات والرسائل)
  const end = (
    <div className="header-icons">
      <Button
        icon="pi pi-bell"
        className="p-button-text p-button-rounded p-button-lg"
        aria-label="Notifications"
      >
        <Badge value="3" severity="danger" />
      </Button>
      <Button
        icon="pi pi-envelope"
        className="p-button-text p-button-rounded p-button-lg"
        aria-label="Messages"
      >
        <Badge value="7" severity="info" />
      </Button>
      <Button
        icon="pi pi-user"
        className="p-button-text p-button-rounded p-button-lg"
        aria-label="User Profile"
      />
    </div>
  );

  return (
    <div className="header-container">
      <div className="header-logo">
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/0/05/Facebook_Logo_(2019).png"
          alt="Logo"
          height="40"
          style={{ cursor: "pointer" }}
          onClick={() => (window.location.href = "/")}
        />
      </div>

      <div className="header-search">
        <InputText placeholder="Search Facebook" className="search-input" />
        <i className="pi pi-search search-icon"></i>
      </div>

      <Menubar model={items} className="header-menubar" end={end} />
    </div>
  );
};

export default Header;
