import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { BASE_URL, LOGIN } from "../../Api/APi";
import Loading from "../../Loading/Loading";
import Cookie from "cookie-universal";
import googleIcon from "../../assets/download.png";
import "../../css/google.css";
const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setloading] = useState(false);
  const navigate = useNavigate();
  // cookie
  const cookie = Cookie();
  const handleSubmit = async (e) => {
    e.preventDefault();
    setloading(true);
    try {
      const res = await axios.post(`${BASE_URL}/${LOGIN}`, {
        email,
        password,
      });
      const token = res.data.data.token;

      cookie.set("Bearer", token);
      setloading(false);
      setMessage("User logged in successfully");
      navigate("/dashboard/users");
    } catch (error) {
      setloading(false);
      if (error.response && error.response.status === 404) {
        setMessage("Wrong Email or Password");
      } else {
        setMessage("An error occurred. Please try again.");
      }
    }
  };

  return (
    <>
      {loading && <Loading />}
      <div className="container">
        <div className="row h-100">
          <form onSubmit={handleSubmit} className="form custom-form">
            <div className="custom-form">
              <h1>Login Now</h1>
            </div>

            <div className="form-control">
              <div style={{ width: "50%" }}>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  placeholder="example@mail.com"
                  style={{ width: "100%" }}
                />
                <label htmlFor="email">Email</label>
              </div>
            </div>

            <div className="form-control">
              <div style={{ width: "50%" }}>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="********"
                  minLength={6}
                  style={{ width: "100%" }}
                />
                <label htmlFor="password">Password</label>
              </div>
            </div>

            <div>
              <button type="submit" className="btn btn-primary">
                Login
              </button>{" "}
              <div className="google-btn">
                <a href="http://localhost:4000/api/auth/google">
                  <div className="google-icon-wrapper">
                    <img
                      src={googleIcon}
                      alt="sign in with google"
                      className="google-icon"
                    />
                  </div>
                  <div>
                    <p className="btn-text">
                      <b>sign in with google</b>
                    </p>
                  </div>
                </a>
              </div>
              {message && (
                <span className="error" style={{ width: "40%" }}>
                  {message}
                </span>
              )}
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
